entOid":"7caa03e796bc10fd42dd1f7f0e9a982ba1bedd7b"},"path":"plugins/git_status.sh","currentUser":null,"blob":{"rawLines":["#!/bin/sh","","# Git status plugin for CliFM","# Written by L. Abramovich","# License: GPL3","","# Description: Check if the current directory is inside a git work","# tree, and, if true, print status, i.e., branch name and non","# commited/tracked files","","# NOTE: This script is not intended to be used as a normal plugin,","# that is, called via an action name, but rather to be executed as","# a prompt command (see the configuration file)","","# Some useful git commands for a more complex output:","","#am I inside git repo? [ \"$(git rev-parse --is-inside-work-tree)\" = \"true\" ] && echo \"Yes\"","#current remote/repo: git rev-parse --abbrev-ref @{upstream}","#current repo: git describe --contains --all HEAD","#total upstream commits: git rev-list --count @{upstream}","#last local commit short hash: git rev-parse --short HEAD","#last upstream commit short hash: git rev-parse --short @{upstream}","#Is something stashed: git rev-parse --verify --quiet refs/stash","#Non-commited/tracked local changes: git status | grep -q \"nothing to commit\" && echo \"No\" || echo \"Yes\" or git status -sb","","if [ -n \"$1\" ] && { [ \"$1\" = \"--help\" ] || [ \"$1\" = \"-h\" ]; }; then","\tprintf \"Print current git status (if in a git repository). This script is intended to be executed as a prompt command. Add the absolute path to this plugin to the PROMPT COMMANDS section in the configuration file.\\n\"","\texit 0","fi","","[ \"$(git rev-parse --is-inside-work-tree 2>/dev/null)\" != \"true\" ] && exit 1","","if [ -n \"$CLIFM_COLORLESS\" ]; then","\tgit status -sb 2>/dev/null | cat","else","\tgit status -sb 2>/dev/null","fi","","exit 0"],"stylingDirectives":[[{"start":0,"end":9,"cssClass":"pl-c"},{"start":0,"end":2,"cssClass":"pl-c"}],[],[{"start":0,"end":29,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":0,"end":26,"cssClass":"pl-c"},{